INSERT INTO tb_game_list (name) VALUES ('Simulation Games');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('The Binding of Isaac: Rebirth', 4.5, 2014, 'Roguelike, Shooter', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/Brendon3578/java-dslist/main/game-images/the-binding-of-isaac-rebirth.jpg', 'A randomly generated action RPG shooter.', 'Isaac escapes into the basement facing droves of deranged enemies, lost brothers and sisters, his fears, and eventually his mother.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Terraria', 4.6, 2011, 'Action, Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/Brendon3578/java-dslist/main/game-images/terraria.jpg', 'A sandbox adventure game.', 'Dig, fight, explore, build! Nothing is impossible in this action-packed adventure game. The world is your canvas and the ground itself is your paint.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Celeste', 4.7, 2018, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/Brendon3578/java-dslist/main/game-images/celeste.jpg', 'A platformer about climbing a mountain.', 'Help Madeline survive her inner demons on her journey to the top of Celeste Mountain, in this super-tight, hand-crafted platformer from the creators of TowerFall.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Half-Life 2', 4.8, 2004, 'Shooter', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/Brendon3578/java-dslist/main/game-images/half-life-2.jpg', 'A first-person shooter game.', 'Half-Life 2 takes the suspense, challenge and visceral charge of the original, and adds startling new realism and responsiveness.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Minecraft', 4.8, 2011, 'Sandbox, Survival', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/Brendon3578/java-dslist/main/game-images/minecraft.jpg', 'A sandbox game about placing blocks and going on adventures.', 'Explore randomly generated worlds and build amazing things from the simplest of homes to the grandest of castles.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Cookie Clicker', 4.2, 2013, 'Idle', 'PC', 'https://raw.githubusercontent.com/Brendon3578/java-dslist/main/game-images/cookie-clicker.jpg', 'An idle clicker game.', 'Bake millions of cookies with a few simple clicks and watch the numbers skyrocket.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Blasphemous', 4.5, 2019, 'Action, Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/Brendon3578/java-dslist/main/game-images/blasphemous.jpg', 'A punishing action-platformer.', 'Play as The Penitent One in a world twisted by dark forces and religious fervor. Explore, fight and survive in this brutal action-platformer.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Stardew Valley', 4.7, 2016, 'Simulation, RPG', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/Brendon3578/java-dslist/main/game-images/stardew-valley.jpg', 'An open-ended country-life RPG.', 'Turn your overgrown field into a lively farm! Raise animals, grow crops, start an orchard, craft useful machines, and much more!');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Dead Cells', 4.6, 2017, 'Roguelike, Action', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/Brendon3578/java-dslist/main/game-images/dead-cells.jpg', 'A roguelike action-platformer.', 'Explore a sprawling, ever-changing castle... assuming you’re able to fight your way past its keepers in 2D souls-lite combat.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Slime Rancher', 4.4, 2017, 'Simulation, Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/Brendon3578/java-dslist/main/game-images/slime-rancher.jpg', 'A first-person life simulation adventure game.', 'Play as Beatrix LeBeau: a plucky, young rancher who sets out for a life on the Far, Far Range, a thousand light years away from Earth.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Rain World', 4.2, 2017, 'Action, Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/Brendon3578/java-dslist/main/game-images/rain-world.jpg', 'A survival platformer.', 'You are a slugcat. The world around you is full of danger, and you must face it alone. Precarious predators lurk around every corner, and the constant torrential downpour threatens to drown all life.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Graveyard Keeper', 4.1, 2018, 'Simulation, RPG', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/Brendon3578/java-dslist/main/game-images/graveyard-keeper.jpg', 'A graveyard-themed management game.', 'Build and manage your own graveyard while finding shortcuts to cut costs, expand into entertainment with witch-burning festivals, and scare nearby villagers into attending church.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Portal', 4.8, 2007, 'Puzzle, Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/Brendon3578/java-dslist/main/game-images/portal.jpg', 'A first-person puzzle-platform game.', 'Solve physical puzzles and challenges by opening portals to maneuver objects, and yourself, through space.');
